#ifndef _CPU_H_
#define _CPU_H_

#include <stdint.h>

// -------------------------------------------------------------------------------------

/**
 * Return from interrupt - skip possible function epilogue generated by compiler
 */
#define reti \
    __asm__(" reti");

/**
 * 16-bit SFR register type
 */
#define hw_register_16(x) \
    (*((volatile uint16_t *)((uint16_t) (x))))


#endif /* _CPU_H_ */
